import React, { useMemo } from "react";

const SummaryCards = ({ data }) => {
  const stats = useMemo(() => {
    const parseNum = (val) => parseFloat(val) || 0;
    let totalOpens = 0;
    let totalClicks = 0;
    let totalSends = 0;
    let totalBounces = 0;

    data.forEach((row) => {
      totalOpens += parseNum(row["Total Opens"]);
      totalClicks += parseNum(row["Total Clicks"]);
      totalSends += parseNum(row["Total Recipients"]);
      totalBounces += parseNum(row["Bounces"]);
    });

    const ctr = totalSends ? (totalClicks / totalOpens) * 100 : 0;
    const openRate = totalSends ? (totalOpens / totalSends) * 100 : 0;
    const bounceRate = totalSends ? (totalBounces / totalSends) * 100 : 0;

    return {
      totalOpens,
      totalClicks,
      totalSends,
      totalBounces,
      ctr,
      openRate,
      bounceRate,
    };
  }, [data]);

  const cardClass =
    "bg-white shadow p-4 rounded-xl w-full sm:w-1/3 lg:w-1/4 flex flex-col gap-1";

  return (
    <div className="flex flex-wrap gap-4">
      <div className={cardClass}>
        <h4 className="text-sm text-gray-500">Total Sends</h4>
        <p className="text-xl font-bold">{stats.totalSends}</p>
      </div>
      <div className={cardClass}>
        <h4 className="text-sm text-gray-500">Total Opens</h4>
        <p className="text-xl font-bold">{stats.totalOpens}</p>
      </div>
      <div className={cardClass}>
        <h4 className="text-sm text-gray-500">Open Rate</h4>
        <p className="text-xl font-bold">{stats.openRate.toFixed(2)}%</p>
      </div>
      <div className={cardClass}>
        <h4 className="text-sm text-gray-500">Total Clicks</h4>
        <p className="text-xl font-bold">{stats.totalClicks}</p>
      </div>
      <div className={cardClass}>
        <h4 className="text-sm text-gray-500">CTR</h4>
        <p className="text-xl font-bold">{stats.ctr.toFixed(2)}%</p>
      </div>
      <div className={cardClass}>
        <h4 className="text-sm text-gray-500">Bounces</h4>
        <p className="text-xl font-bold">{stats.totalBounces}</p>
      </div>
      <div className={cardClass}>
        <h4 className="text-sm text-gray-500">Bounce Rate</h4>
        <p className="text-xl font-bold">{stats.bounceRate.toFixed(2)}%</p>
      </div>
    </div>
  );
};

export default SummaryCards;
